import React from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  return (
    <div className="App">
      <h1>Calcolo Combinatorio</h1>
      <fieldset>
        <legend>Operazione da eseguire</legend>
        {
          LABELS.map((label, key) => (
            <label key={key}>
              <input
                type="radio"
                name="operazione"
                value={label[0].toLowerCase() + label.slice(1, label.length).replaceAll(" ", "")}
                checked={operazioneSelezionata === label[0].toLowerCase() + label.slice(1, label.length).replaceAll(" ", "")}
                onChange={() => modificaOperazione(label[0].toLowerCase() + label.slice(1, label.length).replaceAll(" ", "") as Operazione)}
              />
              {label}
            </label>
          ))
        }
      </fieldset>
      <form>
        <input style={{display: mostraN ? "flex" : "none"}} onChange={(e: any) => setValoreN(e.target.value)} type="number" placeholder='N' />
        <input style={{display: mostraK ? "flex" : "none"}} onChange={(e: any) => setValoreK(e.target.value)} type="number" placeholder='K' />
      </form>
      <button style={{display: mostraN ? "" : "none"}} onClick={() => eseguiOperazione()}>Calcola</button>
        <h2 style={{display: risultato ? "" : "none"}}>Risultato: {risultato}</h2>
    </div>
  );
}

export default App;
